    /* Ephemeral port selection function */
    id_range = max_id - min_id + 1;
    next_id = min_id + (random() % id_range);
    count = next_id;

    do {
        if(check_suitable_id(next_id))
            return next_id;

        if (next_id == max_id) {
            next_id = min_id;
        } else {
            next_id++;
        }

        count--;
    } while (count > 0);

    return ERROR;

